import React from 'react';
import ReactDOM from 'react-dom'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faCoffee } from '@fortawesome/free-solid-svg-icons'
import "./Note.scss";
const Axios = require('axios').default;
const element = <FontAwesomeIcon icon={faCoffee} />

function Note({note, getNote, editNote}) {

    async function deleting() {
        await Axios.delete(`http://localhost:4000/routers/${note._id}`)
        getNote();
    }

    return (

    <div className= "note">
    {note.name && <h2 className="heading">{note.title}</h2>}

    {note.title && <p className="desceription">{note.name}</p>}
    <button className="deleteButton" onClick= {deleting}>{element} </button>
    <button className="editButton" onClick={() => editNote(note)}> Edit </button>


    </div>
    )
};

export default Note;
