import React, {useState, useEffect} from "react";
import Note from "./Note";
// import NoteEditor from "./NoteEditor";
const Axios = require('axios').default;


function Home1() {
    const [notes, setNotes] = useState([]);
    const [newNoteEditorOpen, setNewNoteEditorOpen] = useState(false);

    const [noteTitle, setNoteTitle] = useState([""]);
    const [noteDescription, setNoteDescription] = useState([""]);
    const [noteCode, setNoteCode] = useState([""]);


    useEffect(() => {
        //get note
        getNote();


    }, []);

    async function getNote(){
        const notePath = await Axios.get("http://localhost:4000/routers/");
        setNotes(notePath.data)
    }

    function renderNote(){
        let sortedNote = [...notes];
        sortedNote = sortedNote.sort((a, b) =>{
            return new Date(b.createdAt) - new Date(a.createdAt);
        })  
        return sortedNote.map((note, i) => {
            return <Note key={i} note={note} />

        })


    }

    async function savingNote(event) {
        event.preventDefault();
        const noteData = {
            title: noteTitle ? noteTitle : undefined,
            name: noteDescription ? noteDescription : undefined,
            code: noteCode ? noteCode : undefined
        };

        await Axios.post("http://localhost:4000/routers/", noteData);
        props.getNote();
        cancelNote();

    }

    function cancelNote() {
        props.setNewNoteEditorOpen(false);
        setNoteTitle("");
        setNoteDescription("");
        setNoteCode("");
    }




    return (
        <div className="Home"> 
        {!newNoteEditorOpen && <button onClick={() => setNewNoteEditorOpen(true)}>Add Note</button>}
        {newNoteEditorOpen && (
        <div className= "note-editor">
        <form onSubmit={savingNote}>
            <label htmlFor="editor-title">Title </label>
            <input id="editor-title"  type="text" value = {noteTitle} onChange={(event) => setNoteTitle(event.target.value)}/>

            <label htmlFor="editor-description">Description </label>
            <input id="editor-description"  type="text" value = {noteDescription} onChange={(event) => setNoteDescription(event.target.value)}/>

            <label htmlFor="editor-code">Code</label>
            <textarea id="editor-code" value ={noteCode} onChange={(event) => setNoteCode(event.target.value)}/>

            <button type ="submit">Save Note</button>
            <button type ="button" onClick={cancelNote}>Cancel Note</button>
        </form>

    </div>
            )}
        {renderNote()} 

        </div>
    );
}

export default Home1;
