import React, {useState, useEffect} from "react";
import Note from "./Note";
import NoteEditor from "./NoteEditor";
const Axios = require('axios').default;


function Home1() {
    const [notes, setNotes] = useState([]);
    const [newNoteEditorOpen, setNewNoteEditorOpen] = useState(false);


    useEffect(() => {
        //get note
        getNote();


    }, []);

    async function getNote(){
        const notePath = await Axios.get("http://localhost:4000/routers/");
        setNotes(notePath.data)
    }

    function renderNote(){
        let sortedNote = [...notes];
        sortedNote = sortedNote.sort((a, b) =>{
            return new Date(b.createdAt) - new Date(a.createdAt);
        })  
        return sortedNote.map((note, i) => {
            return <Note key={i} note={note} />

        })


    }




    return (
        <div className="Home"> 
        {!newNoteEditorOpen && <button onClick={() => setNewNoteEditorOpen(true)}>Add Note</button>}
        {newNoteEditorOpen && (
            <NoteEditor setNewNoteEditorOpen ={setNewNoteEditorOpen} getNote={getNote} />
            )}
        {renderNote()} 

        </div>
    );
}

export default Home1;
